formulas.expression:
TEXT(
  DAY(DATEVALUE({!Get_Existing_Event.ActivityDateTime}))
) & " " &
CASE(
  MONTH(DATEVALUE({!Get_Existing_Event.ActivityDateTime})),
  1, "Jan", 2, "Feb", 3, "Mar", 4, "Apr", 5, "May", 6, "Jun",
  7, "Jul", 8, "Aug", 9, "Sep", 10, "Oct", 11, "Nov", 12, "Dec", ""
) & " " &
TEXT(YEAR(DATEVALUE({!Get_Existing_Event.ActivityDateTime}))) & ", " &
RIGHT("0" & TEXT(
  FLOOR(
    MOD(
      ({!Get_Existing_Event.ActivityDateTime} - DATETIMEVALUE("1970-01-01 00:00:00") + (7/24)) * 24,
      24
    )
  )
), 2) & ":" &
RIGHT("0" & TEXT(
  FLOOR(
    MOD(
      ({!Get_Existing_Event.ActivityDateTime} - DATETIMEVALUE("1970-01-01 00:00:00") + (7/24)) * 1440,
      60
    )
  )
), 2)
