formulas.expression:
IF(
    AND(
        FIND('@', {!Asset_Map.value}) > 0,
        FIND(',', {!Asset_Map.value}, FIND('@', {!Asset_Map.value})) > 0
    ),
    IF(
        ISNUMBER(
            TRIM(
                SUBSTITUTE(
                    MID(
                        {!Asset_Map.value}, 
                        FIND('@', {!Asset_Map.value}) + 1, 
                        FIND(',', {!Asset_Map.value}, FIND('@', {!Asset_Map.value})) - FIND('@', {!Asset_Map.value}) - 1
                    ),
                    " ", ""
                )
            )
        ),
        VALUE(
            TRIM(
                SUBSTITUTE(
                    MID(
                        {!Asset_Map.value}, 
                        FIND('@', {!Asset_Map.value}) + 1, 
                        FIND(',', {!Asset_Map.value}, FIND('@', {!Asset_Map.value})) - FIND('@', {!Asset_Map.value}) - 1
                    ),
                    " ", ""
                )
            )
        ),
        0
    ),
    0
)
