ValidationRule.errorConditionFormula:
AND(
  NOT(ISBLANK(SCC_Card_Number__c)),
  NOT(
    AND(
      REGEX(SCC_Card_Number__c, "^[a-zA-Z0-9]+$")
    )
  ),
  NOT($Permission.By_Pass_Validation_for_Migration),
  NOT(
    OR(
      CreatedBy.Alias = 'Migrasi',
      CreatedBy.Alias = 'migration',
      CreatedBy.Alias = 'bhart'
    )
  ),
  NOT(
    AND(
      BEGINS(PRIORVALUE(OwnerId), "003"),
      BEGINS(OwnerId, "00G")
    )
  )
)
