WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/43.0/connection.js")} // Load AJAX toolkit

var lead = new sforce.SObject("Lead");
lead.Id = "{!Lead.Id}";
lead.Status = "Unqualified";

var result = sforce.connection.update([lead]);

if (result[0].success == "true") {
    alert("Lead status updated to Unqualified.");
    location.reload();
} else {
    alert("Error: " + result[0].errors.message);
}
