CustomField.formula:
OR(
  /* Check 1: Digit melebihi 16? */
  Check_Digit_Length__c > 16,

  /* Check 2: Combined value melebihi max Number(18,2)? */
  AND(
    NOT(ISBLANK(Digit_16__c)),
    NOT(ISBLANK(Decimal_2__c)),
    Digit_16__c + Decimal_2__c > 9999999999999999.99
  ),

  /* Check 3: Mismatch antara Number vs Text? */
  AND(
    NOT(ISBLANK(Total_Sum_Insured__c)),
    NOT(ISBLANK(Number_16_2_Text__c)),
    TEXT(Total_Sum_Insured__c) != Number_16_2_Text__c
  )
)
