CustomField.formula:
AND(
   NOT(ISBLANK(Tanggal_Total_Target_SLA__c)),
   IF(
       NOT(ISBLANK(Tanggal_Total_Penyelesaian_SLA__c)),
       /* Jika ada tanggal penyelesaian, bandingkan tanggal penyelesaian dengan target */
       DATETIMEVALUE(
           MID(Tanggal_Total_Penyelesaian_SLA__c, 7, 4) & "-" &
           MID(Tanggal_Total_Penyelesaian_SLA__c, 4, 2) & "-" &
           MID(Tanggal_Total_Penyelesaian_SLA__c, 1, 2) & " " &
           MID(Tanggal_Total_Penyelesaian_SLA__c, 12, 8)
       ) > DATETIMEVALUE(
           MID(Tanggal_Total_Target_SLA__c, 7, 4) & "-" &
           MID(Tanggal_Total_Target_SLA__c, 4, 2) & "-" &
           MID(Tanggal_Total_Target_SLA__c, 1, 2) & " " &
           MID(Tanggal_Total_Target_SLA__c, 12, 8)
       ),
       /* Jika tidak ada tanggal penyelesaian, cek flag Flag_Returned_Ticket__c */
       IF(
           flag_returned_ticket__c,
           FALSE, /* Jika flag TRUE, return FALSE (tidak perlu cek waktu saat ini) */
           /* Jika flag FALSE, bandingkan waktu saat ini dengan target */
           NOW() > DATETIMEVALUE(
               MID(Tanggal_Total_Target_SLA__c, 7, 4) & "-" &
               MID(Tanggal_Total_Target_SLA__c, 4, 2) & "-" &
               MID(Tanggal_Total_Target_SLA__c, 1, 2) & " " &
               MID(Tanggal_Total_Target_SLA__c, 12, 8)
           )
       )
   )
)
